mapper.select:
SELECT COUNT(*)
FROM verification
WHERE email=#{email}
AND code=#{code}

SELECT u_email, u_name, u_bdate, u_gender, u_pwd, u_phone, u_grade
from User
WHERE u_email=#{email} AND u_pwd=#{pwd}

SELECT u_email, u_name, u_bdate, u_gender, u_pwd, u_phone, u_grade
from User
WHERE u_email=#{value}
