mapper.sql:
SELECT COUNT(r_no)
FROM Rate
WHERE to_email = #{email}

SELECT COUNT(CASE WHEN r_text LIKE concat('N','%') THEN 1 END) * 1.0 / COUNT(r_no)
FROM Rate
WHERE to_email = #{email}

mapper.select:
SELECT COUNT(r.r_no)
FROM User u JOIN Rate r ON u.u_email = r.to_email
WHERE u.u_email = #{email}

SELECT COUNT(CASE WHEN r.r_text LIKE concat('N','%') THEN 1 END) * 1.0 / COUNT(r.r_no) AS n_ratio
FROM User u JOIN Rate r ON u.u_email = r.to_email
WHERE u.u_email = #{email}
